import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const BTXPriceChart = () => {
  const [data24h, setData24h] = useState([]);
  const [dataWeek, setDataWeek] = useState([]);
  const [dataMonth, setDataMonth] = useState([]);
  const [currentData, setCurrentData] = useState([]);
  const [currentLabel, setCurrentLabel] = useState('Last 24 Hours');
  const [activePeriod, setActivePeriod] = useState('24h');
  const [additionalData, setAdditionalData] = useState({});
  const [socketError, setSocketError] = useState(null);

  useEffect(() => {
    fetchData('1', setData24h);
    fetchData('7', setDataWeek);
    fetchData('30', setDataMonth);

    // const ws = new WebSocket('ws://localhost:5000');

    // ws.onmessage = (event) => {
    //   const data = JSON.parse(event.data);
    //   if (data.type === 'update' && Object.keys(data.payload).length !== 0) {
    //     setAdditionalData(data.payload);
    //     console.log(data.payload, "data.payload data.payload data.payload ")
    //     console.log(data.payload, "data.payload data.payload data.payload ")
    //   }
    // };

    // ws.onopen = () => {
    //   setSocketError(null);
    // };

    // ws.onerror = (error) => {
    //   setSocketError('WebSocket error');
    // };

    // ws.onclose = () => {};

    // return () => {
    //   ws.close();
    // };
  }, []);

  useEffect(() => {
    setCurrentData(data24h);
  }, [data24h]);

  const fetchData = async (days, setData) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/price-change`, {
        params: { vs_currency: 'usd', days: days },
      });
      const formattedData = response.data.priceData.map((item) => ({
        timestamp: item.timestamp,
        price: item.price,
      }));
      console.log(formattedData, "formattedData formattedData formattedData formattedData")
      setData(formattedData);
      setAdditionalData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleButtonClick = async (period) => {
    switch (period) {
      case '24h':
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
        await fetchData('1', setData24h);
        break;
      case 'week':
        setCurrentData(dataWeek);
        setCurrentLabel('Weekly');
        await fetchData('7', setDataWeek);
        break;
      case 'month':
        setCurrentData(dataMonth);
        setCurrentLabel('Monthly');
        await fetchData('30', setDataMonth);
        break;
      default:
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
        await fetchData('1', setData24h);
    }
    setActivePeriod(period);
  };

  const renderChart = (label, data) => (
    <div className="chart-card">
      <Line
        data={{
          labels: data.map((item) => new Date(item.timestamp)),
          datasets: [
            {
              label: '$',
              data: data.map((item) => item.price),
              borderColor: 'blue',
              backgroundColor: 'rgba(0, 123, 255, 0.2)',
              fill: true,
            },
          ],
        }}
        options={{
          scales: {
            y: {
              display: false,
            },
            x: {
              type: 'time',
              time: {
                unit: 'day',
                tooltipFormat: 'MMM d',
                displayFormats: {
                  day: 'MMM d',
                },
              },
              ticks: {
                callback: function (value, index, values) {
                  const date = new Date(values[index].value);
                  return date.toLocaleString('en-US', { month: 'short', day: 'numeric' });
                },
              },
            },
          },
          plugins: {
            legend: {
              display: false,
            },
            tooltip: {
              displayColors: false,
              callbacks: {
                label: function (context) {
                  let label = '';
                  if (context.parsed.y !== null && context.parsed.y !== undefined) {
                    label += context.parsed.y.toFixed(3);
                  }
                  return label;
                },
              },
            },
          },
        }}
      />
    </div>
  );

  return (
    <div className="container">
      <div className="child-card">
        <div className="overview">
          <b>Overview</b>
          <span className="infobtn" title="BTX Price Chart"><i className="info"></i></span>
        </div>
        <div className="overview">
          Max records
          <span className="hardcoded">{additionalData?.maxRecords}</span>
        </div>
        <div className="overview">
          Comparative rates
          <div className="blue">+<span className="hardcoded">{additionalData?.comparativeRates}</span></div>
        </div>
        <div className="button-container">
          <button className={`tab-button ${activePeriod === '24h' ? 'active' : ''}`} onClick={() => handleButtonClick('24h')}>24</button>
          <button className={`tab-button ${activePeriod === 'week' ? 'active' : ''}`} onClick={() => handleButtonClick('week')}>Week</button>
          <button className={`tab-button ${activePeriod === 'month' ? 'active' : ''}`} onClick={() => handleButtonClick('month')}>Month</button>
        </div>
        <div className="chart-container">
          {renderChart(currentLabel, currentData)}
        </div>
        <div>
          <div className="hardcoded-left">
            +<span className="overview-left">
              <b>{additionalData?.percentageChange?.toFixed(2)}</b>
            </span>
            <span className='percentage'>%</span>
          </div>
          <span className="info-right-grey">Last updated: {new Date(additionalData?.lastUpdated).toLocaleString()}</span>
        </div>
        <div>
          <span className="hardcoded">Today, {new Date().toLocaleTimeString()}</span>
        </div>
      </div>
    </div>
  );
};

export default BTXPriceChart;
