import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

const BTXPriceChart = () => {
  const [data24h, setData24h] = useState([]);
  const [dataWeek, setDataWeek] = useState([]);
  const [dataMonth, setDataMonth] = useState([]);

  useEffect(() => {
    fetchData('24h', setData24h);
    fetchData('7d', setDataWeek);
    fetchData('30d', setDataMonth);
  }, []);

  const fetchData = async (period, setData) => {
    try {
      const response = await axios.get(`https://api.coingecko.com/api/v3/coins/bitcore/market_chart?vs_currency=usd&days=${period}`);
      setData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const renderChart = (label, data) => {
    return (
      <div className="chart-card">
        <h2>{label}</h2>
        <Line
          data={{
            labels: data.map(item => item.timestamp),
            datasets: [{
              label: 'BTX Price',
              data: data.map(item => item.price),
              borderColor: 'blue',
              fill: false,
            }],
          }}
        />
      </div>
    );
  };

  return (
    <div className="container">
      <h1>BTX Price Chart</h1>
      <div className="chart-container">
        {renderChart('Last 24 Hours', data24h)}
        {renderChart('Weekly', dataWeek)}
        {renderChart('Monthly', dataMonth)}
      </div>
    </div>
  );
};

export default BTXPriceChart;
