import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

const BTXPriceChart = () => {
  const [data24h, setData24h] = useState([]);
  const [dataWeek, setDataWeek] = useState([]);
  const [dataMonth, setDataMonth] = useState([]);

  useEffect(() => {
    fetchData('1', setData24h);
    fetchData('7', setDataWeek);
    fetchData('30', setDataMonth);
  }, []);

  const fetchData = async (days, setData) => {
    try {
      const response = await axios.get(`https://api.coingecko.com/api/v3/coins/bitcore/market_chart`, {
        params: {
          vs_currency: 'usd',
          days: days,
        },
      });
      const formattedData = response.data.prices.map((price) => ({
        timestamp: new Date(price[0]).toLocaleString(),
        price: price[1],
      }));
      setData(formattedData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const renderChart = (label, data) => (
    <div className="chart-card">
      <h2>{label}</h2>
      <Line
        data={{
          labels: data.map((item) => item.timestamp),
          datasets: [
            {
              label: 'BTX Price',
              data: data.map((item) => item.price),
              borderColor: 'blue',
              fill: false,
            },
          ],
        }}
      />
    </div>
  );

  return (
    <div className="container">
      <div className="chart-container">
        {renderChart('Last 24 Hours', data24h)}
        {renderChart('Weekly', dataWeek)}
        {renderChart('Monthly', dataMonth)}
      </div>
    </div>
  );
};

export default BTXPriceChart;
