import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from 'chart.js';
import 'chartjs-adapter-date-fns'; // Import the date-fns adapter for Chart.js
import { Line } from 'react-chartjs-2';

// Register the necessary components with Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale // Register the time scale
);

const BTXPriceChart = () => {
  const [data24h, setData24h] = useState([]);
  const [dataWeek, setDataWeek] = useState([]);
  const [dataMonth, setDataMonth] = useState([]);
  const [currentData, setCurrentData] = useState([]);
  const [currentLabel, setCurrentLabel] = useState('Last 24 Hours');
  const [activePeriod, setActivePeriod] = useState('24h');
  const [additionalData, setAdditionalData] = useState(null); // State variable to store additional data

  useEffect(() => {
    fetchData('1', setData24h);
    fetchData('7', setDataWeek);
    fetchData('30', setDataMonth);
  }, []);

  useEffect(() => {
    setCurrentData(data24h);
  }, [data24h]);

  const fetchData = async (days, setData) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/price-change`, {
        params: {
          vs_currency: 'usd',
          days: days,
        },
      });
      console.log(response.data.priceData, " response.data.priceData")
      const formattedData = response.data.priceData.map((item) => ({
        timestamp: item.timestamp,
        price: item.price,
      }));
      setData(formattedData);
      return response.data; // Return the additional data
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleButtonClick = async (period) => {
    let data;
    let additionalData;
    switch (period) {
      case '24h':
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
        additionalData = await fetchData('1', setData24h);
        break;
      case 'week':
        setCurrentData(dataWeek);
        setCurrentLabel('Weekly');
        additionalData = await fetchData('7', setDataWeek);
        break;
      case 'month':
        setCurrentData(dataMonth);
        setCurrentLabel('Monthly');
        additionalData = await fetchData('30', setDataMonth);
        break;
      default:
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
        additionalData = await fetchData('1', setData24h);
    }
    setActivePeriod(period);
    setAdditionalData(additionalData);
  };

  const renderChart = (label, data) => (
    console.log('Data:', data),
    <div className="chart-card">
      <Line
        data={{
          labels: data.map((item) => new Date(item.timestamp)),
          datasets: [
            {
              label: '$',
              data: data.map((item) => item.price),
              borderColor: 'blue',
              backgroundColor: 'rgba(0, 123, 255, 0.2)', // Add background color to fill the area under the line
              fill: true,
            },
          ],
        }}
        options={{
          scales: {
            y: {
              display: false, // Hide the y-axis
            },
            x: {
              type: 'time',
              time: {
                unit: 'day',
                tooltipFormat: 'MMM d',
                displayFormats: {
                  day: 'MMM d',
                },
              },
              ticks: {
                callback: function (value, index, values) {
                  const date = new Date(values[index].value);
                  return date.toLocaleString('en-US', { month: 'short', day: 'numeric' });
                },
              },
            },
          },
          plugins: {
            legend: {
              display: false, // Hide the legend
            },
            tooltip: {
              displayColors: false, // Remove the legend box
              callbacks: {
                label: function (context) {
                  let label = '';
                  if (context.parsed.y !== null) {
                    label += context.parsed.y.toFixed(3);
                  }
                  return label;
                }
              },
              // Use custom CSS class for the tooltip
              bodyFontColor: '#fff',
              bodyFontFamily: "'Helvetica Neue', 'Helvetica', 'Arial', sans-serif",
              bodyFontSize: 20, // This size will be overwritten by the custom CSS
              bodySpacing: 20,
              mode: 'index',
              intersect: false,
              position: 'nearest',
              custom: function (tooltipModel) {
                // Tooltip Element
                var tooltipEl = document.getElementById('chartjs-tooltip');

                // Create element on first render
                if (!tooltipEl) {
                  tooltipEl = document.createElement('div');
                  tooltipEl.id = 'chartjs-tooltip';
                  tooltipEl.classList.add('custom-tooltip');
                  document.body.appendChild(tooltipEl);
                }

                // Hide if no tooltip
                if (tooltipModel.opacity === 0) {
                  tooltipEl.style.opacity = 0;
                  return;
                }

                // Set caret position
                tooltipEl.classList.remove('above', 'below', 'no-transform');
                if (tooltipModel.yAlign) {
                  tooltipEl.classList.add(tooltipModel.yAlign);
                } else {
                  tooltipEl.classList.add('no-transform');
                }

                // Set Text
                if (tooltipModel.body) {
                  var titleLines = tooltipModel.title || [];
                  var bodyLines = tooltipModel.body.map(function (bodyItem) {
                    return bodyItem.lines;
                  });

                  var innerHtml = '<thead>';

                  titleLines.forEach(function (title) {
                    innerHtml += '<tr><th>' + title + '</th></tr>';
                  });
                  innerHtml += '</thead><tbody>';

                  bodyLines.forEach(function (body, i) {
                    var colors = tooltipModel.labelColors[i];
                    var style = 'background:' + colors.backgroundColor;
                    style += '; border-color:' + colors.borderColor;
                    style += '; border-width: 2px';
                    var span = '<span style="' + style + '"></span>';
                    innerHtml += '<tr><td>' + span + body + '</td></tr>';
                  });
                  innerHtml += '</tbody>';

                  var tableRoot = tooltipEl.querySelector('table');
                  tableRoot.innerHTML = innerHtml;
                }

                // `this` will be the overall tooltip
                var position = this._chart.canvas.getBoundingClientRect();

                // Display, position, and set styles for font
                tooltipEl.style.opacity = 1;
                tooltipEl.style.position = 'absolute';
                tooltipEl.style.left = position.left + window.pageXOffset + tooltipModel.caretX + 'px';
                tooltipEl.style.top = position.top + window.pageYOffset + tooltipModel.caretY + 'px';
                tooltipEl.style.fontFamily = tooltipModel._bodyFontFamily;
                tooltipEl.style.fontSize = tooltipModel.bodyFontSize + 'px';
                tooltipEl.style.fontStyle = tooltipModel._bodyFontStyle;
                tooltipEl.style.padding = tooltipModel.yPadding + 'px ' + tooltipModel.xPadding + 'px';
                tooltipEl.style.pointerEvents = 'none';
              }
            }
          }

        }}
      />
    </div>
  );

  return (
    <div className="container">
      <div className="child-card">
        <div className="overview">
          <b>Overview</b>
          <span className="infobtn" title="BTX Price Chart"><i className="info"></i></span>
        </div>
        <div className="overview">
          Max records
          <span className="hardcoded">{additionalData ? additionalData.maxRecords : 'Loading...'}</span>
        </div>
        <div className="overview">
          Comparative rates
          <div className="blue">+<span className="hardcoded">{additionalData ? additionalData.comparativeRates : 'Loading...'}</span></div>
        </div>
        <div className="button-container">
          <button className={`tab-button ${activePeriod === '24h' ? 'active' : ''}`} onClick={() => handleButtonClick('24h')}>24</button>
          <button className={`tab-button ${activePeriod === 'week' ? 'active' : ''}`} onClick={() => handleButtonClick('week')}>Week</button>
          <button className={`tab-button ${activePeriod === 'month' ? 'active' : ''}`} onClick={() => handleButtonClick('month')}>Month</button>
        </div>
        <div className="chart-container">
          {renderChart(currentLabel, currentData)}
        </div>
        <div>
          <div className="hardcoded-left">+<span className="overview-left"><b>{additionalData ? additionalData.percentageChange.toFixed(2) : 'Loading...'}</b></span><span className='percentage'>%</span></div>
          <span className="info-right-grey">Last updated: {additionalData ? new Date(additionalData.lastUpdated).toLocaleString() : 'Loading...'}</span>
        </div>
        <div>
          <span className="hardcoded">Today, {new Date().toLocaleTimeString()}</span>
        </div>
      </div>
    </div>
  );

};

export default BTXPriceChart;
