import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from 'chart.js';
import 'chartjs-adapter-date-fns'; // Import the date-fns adapter for Chart.js
import { Line } from 'react-chartjs-2';

// Register the necessary components with Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale // Register the time scale
);

const BTXPriceChart = () => {
  const [data24h, setData24h] = useState([]);
  const [dataWeek, setDataWeek] = useState([]);
  const [dataMonth, setDataMonth] = useState([]);
  const [currentData, setCurrentData] = useState([]);
  const [currentLabel, setCurrentLabel] = useState('Last 24 Hours');
  const [activePeriod, setActivePeriod] = useState('24h');

  useEffect(() => {
    fetchData('1', setData24h);
    fetchData('7', setDataWeek);
    fetchData('30', setDataMonth);
  }, []);

  useEffect(() => {
    setCurrentData(data24h);
  }, [data24h]);

  const fetchData = async (days, setData) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/price-change?`, {
        params: {
          vs_currency: 'usd',
          days: days,
        },
      });
      const formattedData = response.data.prices.map((price) => ({
        timestamp: price[0],
        price: price[1],
      }));
      setData(formattedData);
      // Set additional data
      setMaxRecords(response.data.maxRecords);
      setComparativeRates(response.data.comparativeRates);
      setLastUpdated(response.data.lastUpdated);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleButtonClick = (period) => {
    switch (period) {
      case '24h':
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
        break;
      case 'week':
        setCurrentData(dataWeek);
        setCurrentLabel('Weekly');
        break;
      case 'month':
        setCurrentData(dataMonth);
        setCurrentLabel('Monthly');
        break;
      default:
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
    }
    setActivePeriod(period);
  };

  const renderChart = (label, data) => (
    <div className="chart-card">
      <Line
        data={{
          labels: data.map((item) => new Date(item.timestamp)),
          datasets: [
            {
              label: '$',
              data: data.map((item) => item.price),
              borderColor: 'blue',
              backgroundColor: 'rgba(0, 123, 255, 0.2)',
              fill: true,
            },
          ],
        }}
        options={{
          scales: {
            y: { display: false },
            x: {
              type: 'time',
              time: { unit: 'day', tooltipFormat: 'MMM d', displayFormats: { day: 'MMM d' } },
              ticks: {
                callback: function (value, index, values) {
                  const date = new Date(values[index].value);
                  return date.toLocaleString('en-US', { month: 'short', day: 'numeric' });
                },
              },
            },
          },
          plugins: { legend: { display: false }, tooltip: { /* your tooltip settings */ } },
        }}
      />
    </div>
  );

  return (
    <div className="container">
      <div className="child-card">
        <div class="overview">
          <b>Overview</b>
          <span class="infobtn" title="BTX Price Chart"><i class="info"></i></span>
        </div>
        <div class="overview">
          Max records
          <span class="hardcoded">{maxRecords}</span>
        </div>
        <div class="overview">
          Comparative rates
          <div class="blue">{comparativeRates}</div>
        </div>
        <div className="button-container">
          <button className={`tab-button ${activePeriod === '24h' ? 'active' : ''}`} onClick={() => handleButtonClick('24h')}>24</button>
          <button className={`tab-button ${activePeriod === 'week' ? 'active' : ''}`} onClick={() => handleButtonClick('week')}>Week</button>
          <button className={`tab-button ${activePeriod === 'month' ? 'active' : ''}`} onClick={() => handleButtonClick('month')}>Month</button>
        </div>
        <div className="chart-container">
          {renderChart(currentLabel, currentData)}
        </div>
        <div>
          <div class="hardcoded-left">{lastUpdated}</div>
          <span class="info-right-grey">Last updated</span>
          <br />
          <br />
        </div>
        <div>
          <span class="hardcoded">Today, 06:49 AM</span>
        </div>
      </div>
    </div>
  );
};

export default BTXPriceChart;
