import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from 'chart.js';
import 'chartjs-adapter-date-fns'; // Import the date-fns adapter for Chart.js
import { Line } from 'react-chartjs-2';

// Register the necessary components with Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale // Register the time scale
);

const BTXPriceChart = () => {
  const [data24h, setData24h] = useState([]);
  const [dataWeek, setDataWeek] = useState([]);
  const [dataMonth, setDataMonth] = useState([]);
  const [currentData, setCurrentData] = useState([]);
  const [currentLabel, setCurrentLabel] = useState('Last 24 Hours');
  const [activePeriod, setActivePeriod] = useState('24h');

  useEffect(() => {
    fetchData('1', setData24h);
    fetchData('7', setDataWeek);
    fetchData('30', setDataMonth);
  }, []);

  useEffect(() => {
    setCurrentData(data24h);
  }, [data24h]);

  const fetchData = async (days, setData) => {
    try {
      const response = await axios.get(`https://api.coingecko.com/api/v3/coins/bitcore/market_chart`, {
        params: {
          vs_currency: 'usd',
          days: days,
        },
      });
          // Calculate percentage change
    const firstPrice = prices[0].price;
    const lastPrice = prices[prices.length - 1].price;
    const percentageChange = ((lastPrice - firstPrice) / firstPrice) * 100;
      const formattedData = response.data.prices.map((price) => ({
        timestamp: price[0],
        price: price[1],
        ercentageChange: percentageChange.toFixed(2), // Format percentage to two decimal places
      }));
      setData(formattedData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleButtonClick = (period) => {
    switch (period) {
      case '24h':
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
        break;
      case 'week':
        setCurrentData(dataWeek);
        setCurrentLabel('Weekly');
        break;
      case 'month':
        setCurrentData(dataMonth);
        setCurrentLabel('Monthly');
        break;
      default:
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
    }
    setActivePeriod(period);
  };

  const renderChart = (label, data) => (
    <div className="chart-card">
      {/* <h2>{label}</h2> */}
      <Line
        data={{
          labels: data.map((item) => new Date(item.timestamp)),
          datasets: [
            {
              label: '$',
              data: data.map((item) => item.price),
              borderColor: 'blue',
              backgroundColor: 'rgba(0, 123, 255, 0.2)', // Add background color to fill the area under the line
              fill: true,
            },
          ],
        }}
        options={{
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'day',
                tooltipFormat: 'MMM d',
                displayFormats: {
                  day: 'MMM d',
                },
              },
              ticks: {
                callback: function (value, index, values) {
                  const date = new Date(values[index].value);
                  return date.toLocaleString('en-US', { month: 'short', day: 'numeric' });
                },
              },
            },
          },
          plugins: {
            legend: {
              display: false, // Hide the legend
            },
            tooltip: {
                callbacks: {
                  label: function(context) {
                    let label = context.dataset.label || '';
                    if (label) {
                      label += ': ';
                    }
                    if (context.parsed.y !== null) {
                      label += context.parsed.y.toFixed(2); // Display price with 2 decimal places
                      const firstPrice = context.dataset.data[0].y;
                      const currentPrice = context.parsed.y;
                      const percentageChange = ((currentPrice - firstPrice) / firstPrice * 100).toFixed(2);
                      label += ' (' + percentageChange + '%)'; // Calculate and display percentage change
                    }
                    return label;
                  }
                }
              }
          },
        }}
      />
    </div>
  );

  return (
    <div className="container">

      <div className="child-card">
          <div class="overview">
            <b>Overview</b>
            <span class="infobtn"><i class="info"></i></span>  
        </div>
        <div className="button-container">
          <button className={`tab-button ${activePeriod === '24h' ? 'active' : ''}`} onClick={() => handleButtonClick('24h')}>24</button>
          <button className={`tab-button ${activePeriod === 'week' ? 'active' : ''}`} onClick={() => handleButtonClick('week')}>Week</button>
          <button className={`tab-button ${activePeriod === 'month' ? 'active' : ''}`} onClick={() => handleButtonClick('month')}>Month</button>
        </div>
        <div className="chart-container">
          {renderChart(currentLabel, currentData)}
        </div>
      </div>
    </div>
  );
};

export default BTXPriceChart;
