import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from 'chart.js';
import 'chartjs-adapter-date-fns'; // Import the date-fns adapter for Chart.js
import { Line } from 'react-chartjs-2';

// Register the necessary components with Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale // Register the time scale
);

const BTXPriceChart = () => {
  const [data24h, setData24h] = useState([]);
  const [dataWeek, setDataWeek] = useState([]);
  const [dataMonth, setDataMonth] = useState([]);
  const [currentData, setCurrentData] = useState([]);
  const [currentLabel, setCurrentLabel] = useState('Last 24 Hours');

  useEffect(() => {
    fetchData('1', setData24h);
    fetchData('7', setDataWeek);
    fetchData('30', setDataMonth);
  }, []);

  useEffect(() => {
    setCurrentData(data24h);
  }, [data24h]);

  const fetchData = async (days, setData) => {
    try {
      const response = await axios.get(`https://api.coingecko.com/api/v3/coins/bitcore/market_chart`, {
        params: {
          vs_currency: 'usd',
          days: days,
        },
      });
      const formattedData = response.data.prices.map((price) => ({
        timestamp: price[0],
        price: price[1],
      }));
      setData(formattedData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleButtonClick = (period) => {
    switch (period) {
      case '24h':
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
        break;
      case 'week':
        setCurrentData(dataWeek);
        setCurrentLabel('Weekly');
        break;
      case 'month':
        setCurrentData(dataMonth);
        setCurrentLabel('Monthly');
        break;
      default:
        setCurrentData(data24h);
        setCurrentLabel('Last 24 Hours');
    }
  };

  const renderChart = (label, data) => (
    <div className="chart-card">
      {/* <h2>{label}</h2> */}
      <Line
        data={{
          labels: data.map((item) => new Date(item.timestamp)),
          datasets: [
            {
              label: 'BTX Price',
              data: data.map((item) => item.price),
              borderColor: 'blue',
              backgroundColor: 'rgba(0, 123, 255, 0.2)', // Add background color to fill the area under the line
              fill: true,
            },
          ],
        }}
        options={{
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'day',
                tooltipFormat: 'MMM d',
                displayFormats: {
                  day: 'MMM d',
                },
              },
              ticks: {
                callback: function (value, index, values) {
                  const date = new Date(values[index].value);
                  return date.toLocaleString('en-US', { month: 'short', day: 'numeric' });
                },
              },
            },
          },
          plugins: {
            legend: {
              display: false, // Hide the legend
            },
          },
        }}
      />
    </div>
  );

  return (
    <div className="container">

      <div className="child-card">
          <div>
            <strong>Overview</strong>
            <span class="v">&times;</span>  
        </div>
        <div className="button-container">
          <button className="tab-button" onClick={() => handleButtonClick('24h')}>Last 24 Hours</button>
          <button className="tab-button" onClick={() => handleButtonClick('week')}>Weekly</button>
          <button className="tab-button" onClick={() => handleButtonClick('month')}>Monthly</button>
        </div>
        <div className="chart-container">
          {renderChart(currentLabel, currentData)}
        </div>
      </div>
    </div>
  );
};

export default BTXPriceChart;
